<?php

namespace App\Enums;

use Filament\Support\Contracts\HasLabel;

enum FinancialCategory: string implements HasLabel
{
    case Income = 'income';
    case Expense = 'expense';

    public function getLabel(): string
    {
        return match ($this) {
            self::Income => __('general.income'),
            self::Expense => __('general.expense'),
        };
    }
}
</output>
</tool_result>
</tool_calls>

